import React, { Component } from 'react'
import axios from 'axios'
import Main from '../template/Main'

const baseUrl = 'http://localhost:3000/Equipamentos';
// Estado inicial de cadastro de equipamento
const initialState = {
    Alarmes: { id: '' , nomeEquipamento: '' , numeroSerie: '' , tipo: '' , dataCadastro: '' },
    list: []
};

export default class AlarmCrud extends Component {

    state = {...initialState};

    componentWillMount(){
        axios(baseUrl).then(resp => {
            this.setState({ list: resp.data})
        })
    };

    clear() {
        this.setState({ Alarmes: initialState.Alarmes })
    };

    save() {
        const equipment = this.state.equipment;
        const method = equipment.id ? 'put' : 'post';
        const url = equipment.id ? `${baseUrl}/${equipment.id}` : baseUrl;
        axios[method](url,equipment)
            .then(resp => {
                const list = this.getUpdateList(resp.data);
                this.setState({ equipment: initialState.equipment, list})
            })
    };

    getUpdateList(equipment, add = true) {
        const list = this.state.list.filter(newid => newid.id !== equipment.id);
        if(add) list.unshift(equipment)
        return list
    };

    updateField(event) {
        const equipment = {...this.state.equipment }
        equipment[event.target.name] = event.target.value
        this.setState ({ equipment })
    };

    renderForm() {
        return (
            <div className="form">
                <div className="row">
                    <div className="col-12 col-md-6">
                        <div className="form-group">
                            <label><strong>Nome</strong></label>
                            <input type="text" className="form-control"
                                   name="descAlarme"
                                   value={this.state.equipment}
                                   onChange={e => this.updateField(e)}
                                   placeholder="Digite o nome do equipamento" />
                        </div>
                    </div>

                    <div className="col-12 col-md-6">
                        <div className="form-group">
                            <label><strong>Nº de série</strong></label>
                            <input type="text" className="form-control"
                                   name="equipamentoRelacionado"
                                   value={this.state.equipment}
                                   onChange={e => this.updateField(e)}
                                   placeholder="Digite número de série do equipamento" />
                        </div>
                    </div>

                    <div className="col-12 col-md-6">
                        <div className="form-group">
                            <label><strong>Tipo</strong></label>
                            <input type="text" className="form-control"
                                   name="classificacao"
                                   value={this.state.equipment}
                                   onChange={e => this.updateField(e)}
                                   placeholder="Digite o tipo(Tensão,corrente ou óleo)" />
                        </div>
                    </div>
                    <div className="col-12 col-md-6">
                        <div className="form-group">
                            <label><strong>Data de Cadastro</strong></label>
                            <input type="text" className="form-control"
                                   name="dataCadastro"
                                   value={this.state.equipment}
                                   onChange={e => this.updateField(e)}
                                   placeholder="Digite a data"/>
                        </div>
                    </div>


                    <div className="row col-12 col-md-12 d-flex justify-content-lg-between">
                        <button className="col-12 col-md-2 btn btn-danger ml-3"
                                onClick={e => this.clear(e)}>
                            Limpar
                        </button>
                        <button className="col-md-2 btn btn-success ml-2"
                                onClick={e => this.save(e)}>
                            Salvar
                        </button>
                    </div>
                </div>
            </div>
        )

    }

    load(equipment) {
        this.setState({ equipment })
    };

    remove(equipment) {
        axios.delete(`${baseUrl}/${equipment.id}`).then(resp => {
            const list = this.getUpdateList(equipment, false)
            this.setState({ list })
        })
    };

    renderTable() {
        return(
            <table className="table mt-4">
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Nome</th>
                    <th>Nº de Série</th>
                    <th>Tipo</th>
                    <th>Data de Cadastro</th>
                    <th>Ações</th>
                </tr>
                </thead>
                <tbody>
                {this.renderRows()}
                </tbody>
            </table>
        )
    };

    renderRows() {
        return this.state.list.map(equipment =>{
            return(
                <tr key={equipment.id}>
                    <td>{equipment.id}</td>
                    <td>{equipment.nomeEquipamento}</td>
                    <td>{equipment.numeroSerie}</td>
                    <td>{equipment.tipo}</td>
                    <td>{equipment.dataCadastro}</td>
                    <td>
                        <button className="btn btn-danger ml-2"
                                onClick={() => this.remove(equipment)}>
                            <i className="fa fa-trash"></i>
                        </button>
                    </td>
                </tr>
            )
        })
    };
    render () {
        return (
            <Main>
                {this.renderForm()}
                {this.renderTable()}
            </Main>
        )
    }
};
