import React, { Component } from 'react'
import axios from 'axios'
import Main from '../template/Main'

const baseUrl = 'http://localhost:3001/AlarmesAtuados'
// Estado inicial
const initialState = {
    alarm: { name: '' , email: '' },
    list: []
};

const headerProps = {
    icon: 'users',
    title: 'Usuários',
    subtitle: 'Cadastro de usuários: Incluir, Listar,Alterar e Excluir'
};

export default class UserCrud extends Component {

    state = {...initialState}

    componentWillMount(){
        axios(baseUrl).then(resp => {
            this.setState({ list: resp.data})
        })
    }

    clear() {
        this.setState({ alarm: initialState.alarm })
    }

    save() {
        const alarm = this.state.alarm
        const method = alarm.id ? 'put' : 'post'
        const url = alarm.id ? `${baseUrl}/${alarm.id}` : baseUrl
        axios[method](url,alarm)
            .then(resp => {
                const list = this.getUpdateList(resp.data)
                this.setState({ alarm: initialState.alarm, list})
            })
    }

    getUpdateList(alarm, add = true) {
        const list = this.state.list.filter(u => u.id !== alarm.id)
        if(add) list.unshift(alarm)
        return list
    }

    updateField(event) {
        const alarm = {...this.state.alarm }
        alarm[event.target.name] = event.target.value
        this.setState ({ alarm })
    }

    load(alarm) {
        this.setState({ alarm })
    };

    remove(alarm) {
        axios.delete(`${baseUrl}/${alarm.id}`).then(resp => {
            const list = this.getUpdateList(alarm, false)
            this.setState({ list })
        })
    };

    renderTable() {
        return(
            <table className="table mt-4">
                <thead>
                <tr>
                    <th>Id do Alarme</th>
                    <th>Status</th>
                    <th>Urgente</th>
                    <th>Ações</th>
                </tr>
                </thead>
                <tbody>
                {this.renderRows()}
                </tbody>
            </table>
        )
    };

    renderRows() {
        return this.state.list.map(alarm =>{
            return(
                <tr key={alarm.id}>
                    <td>{alarm.id}</td>
                    <td>{alarm.name}</td>
                    <td>{alarm.email}</td>
                    <td>
                        <button className="btn btn-warning"
                                onClick={() => this.load(alarm)}>
                            <i className="fa fa-pencil"></i>
                        </button>
                        <button className="btn btn-danger ml-2"
                                onClick={() => this.remove(alarm)}>
                            <i className="fa fa-trash"></i>
                        </button>
                    </td>
                </tr>
            )
        })
    };
    render () {
        return (
            <Main >
                <div className='display-4'>Gerencimaneto de alarmes</div>
                {this.renderTable()}
            </Main>
        )
    }
};
