import React, { Component } from 'react'
import axios from 'axios'
import Main from '../template/Main'

const baseUrl = 'http://localhost:3000/AlarmesAtuados'
// Estado inicial
const initialState = {
    alarm: { dataEntrada: '' , dataSaida: '' , descAlarm: '' , descEquipamento: '' , id: '' },
    list: []
};
export default class Management extends Component {

    state = {...initialState}

    componentWillMount(){
        axios(baseUrl).then(resp => {
            this.setState({ list: resp.data})
        })
    }

    updateField(event) {
        const alarm = {...this.state.alarm }
        alarm[event.target.name] = event.target.value
        this.setState ({ alarm })
    }

    load(alarm) {
        this.setState({ alarm })
    };

    renderTable() {
        return(
            <table className="table mt-4">
                <thead>
                <tr>
                    <th>Id do Alarme</th>
                    <th>Status</th>
                    <th>Descrição do Alarme</th>
                    <th>Urgente?</th>
                    <th>Ativar/Desativar Alarme</th>
                </tr>
                </thead>
                <tbody>
                {this.renderRows()}
                </tbody>
            </table>
        )
    };

    renderRows() {
        return this.state.list.map(alarm =>{
                return(
                    <tr key={alarm.id}>
                        <td>{alarm.id}</td>
                        <td>{alarm.statusAlarme}</td>
                        <td>{alarm.descAlarm}</td>
                        <td>
                            <button className="btn btn-outline-primary"
                                    onClick={() => this.load(alarm)}>
                                <i className="fa fa-envelope-o"></i>
                            </button>
                        </td>

                        <td>
                            <button className="btn btn-danger ml-2"
                                    onClick={() => this.remove(alarm)}>
                                <i className="fa fa-check-square"></i>
                            </button>

                            <button className="btn btn-danger ml-2"
                                    onClick={() => this.remove(alarm)}>
                                <i className="fa fa-"></i>
                            </button>
                        </td>
                    </tr>

                )
            })
        };
    render () {
        return (
            <Main >
                <div className='display-4'>Gerenciamento de alarmes</div>
                <div className='display-5'>Alarmes que mais atuaram no sistema:</div>
                {this.renderTable()}
            </Main>
        )
    }
};
