import React, { Component } from 'react'
import axios from 'axios'
import Main from '../template/Main'

const baseUrl = 'http://localhost:3001/AlarmesAtuados'
// Estado inicial
const initialState = {
    alarm: { dataEntrada: '' , dataSaida: '' , descAlarm: '' , descEquipamento: '' , id: '' },
    list: []
};

const headerProps = {
    icon: 'users',
    title: 'Usuários',
    subtitle: 'Cadastro de usuários: Incluir, Listar,Alterar e Excluir'
};

export default class UserCrud extends Component {

    state = {...initialState}

    componentWillMount(){
        axios(baseUrl).then(resp => {
            this.setState({ list: resp.data})
        })
    }

    updateField(event) {
        const alarm = {...this.state.alarm }
        alarm[event.target.name] = event.target.value
        this.setState ({ alarm })
    }

    load(alarm) {
        this.setState({ alarm })
    };

    renderTable() {
        return(
            <table className="table mt-4">
                <thead>
                <tr>
                    <th>Id do Alarme</th>
                    <th>Status</th>
                    <th>Urgente</th>
                    <th>Ações</th>
                </tr>
                </thead>
                <tbody>
                {this.renderRows()}
                </tbody>
            </table>
        )
    };

    renderRows() {
        return this.state.list.map(alarm =>{
            return(
                <tr key={alarm.id}>
                    <td>{alarm.id}</td>
                    <td>{alarm.name}</td>
                    <td>{alarm.email}</td>
                    <td>
                        <button className="btn btn-warning"
                                onClick={() => this.load(alarm)}>
                            <i className="fa fa-pencil"></i>
                        </button>
                        <button className="btn btn-danger ml-2"
                                onClick={() => this.remove(alarm)}>
                            <i className="fa fa-trash"></i>
                        </button>
                    </td>
                </tr>
            )
        })
    };
    render () {
        return (
            <Main >
                <div className='display-4'>Gerencimaneto de alarmes</div>
                {this.renderTable()}
            </Main>
        )
    }
};
